package service

import (
	"testing"

	"gitlab.lan/Rightnao-site/microservices/company/pkg/internal/account"
	"gitlab.lan/Rightnao-site/microservices/company/pkg/internal/company-errors"
	"gitlab.lan/Rightnao-site/microservices/company/pkg/internal/profile"
)

func TestAboutUsValidator(t *testing.T) {

	tables := []struct {
		AboutUs *profile.AboutUs
		Result  error
	}{
		{
			AboutUs: &profile.AboutUs{
				Description: "",
				Mission:     "2232",
				Parking:     account.ParkingParkingLot,
				Type:        account.TypePublicCompany,
				Size:        account.SizeFrom10001AndMoreEmployees,
			},
			Result: companyErrors.Max2000,
		},
		{
			AboutUs: &profile.AboutUs{
				Description: "2",
				Mission:     "2232",
				Parking:     account.Parking(""),
				Type:        account.TypePublicCompany,
				Size:        account.SizeFrom10001AndMoreEmployees,
			},
			Result: companyErrors.InvalidEnter,
		},
	}

	for _, ta := range tables {
		err := aboutUsValidator(ta.AboutUs)
		if err != ta.Result {
			t.Errorf("Error: got %v, expected: %v", err, nil)
		}
	}
}
