package service

import (
	"testing"

	"gitlab.lan/Rightnao-site/microservices/user/pkg/internal/users-errors"
)

func TestStoryValidator(t *testing.T) {

	tables := []struct {
		Story  string
		Result error
	}{
		{
			Story:  "",
			Result: nil,
		},
		{
			Story:  "99559861709",
			Result: nil,
		},
		{
			Story:  "",
			Result: usersErrors.Max1200,
		},
	}

	for _, ta := range tables {
		err := storyValidator(ta.Story)
		if err != ta.Result {
			t.Errorf("Error: TestStoryValidator(%v): got %v, expected: %v", ta.Story, err, nil)
		}
	}
}
